INSERT INTO `sphr_object` SET `id`='596dbb51-3b1d-4471-b65b-9c41972af126', `name`='дом Инсула в комплексе Корсега', `date_entered`='2012-08-10 00:00:00', `date_modified`='2011-11-02 01:50:35', `modified_user_id`='1', `created_by`='1', `deleted`='0', `assigned_user_id`='1';
INSERT INTO `sphr_object_cstm` SET `id_c`='596dbb51-3b1d-4471-b65b-9c41972af126', `name_rus_c`='дом Инсула в комплексе Корсега', `name_eng_c`='Semidetached house Insula in urb. Corcega', `name_sp_c`='Adosados Insula residencial Corcega', `amount_commission_c`='', `number_bedroom_c`='2', `number_bathroom_c`='2', `sea_distance_c`='3KM', `comm_center_distance_c`='300M', `additional_description_c`='Спаренный дом построенный в современном дизайне с великолепными видами на гольф поле.В урбанизации Вильямартин у подножия гольфовых полей расположился комплекс Корсега. Комплекс состоит из смежный домов разного типа. Модель Инсула располагает 2 или 3 спальнями на выбор, 2 ванными комнатами, просторным салоном, меблированной кухней, большим солярием 57.64M2 и террасами. У модели с 2 спальнями верх занимает солярий, у модели с тремя спальнями помимо солярия есть спальня и ванная комната. Каждый дом имеет собственный сад с парковочным местом. Все дома выполнены в стиле \"модерн\" с использованием материалами высочайшего качества. Комплекс Корсега - закрытая частная урбанизация с бассейнами и зелеными зонами. Урбанизация Вильямартин имеет развитую инфраструктуру со всеми сервисами и дорожное сообщение. До международного аэропорта Аликанте 47км.Adosados de 2 y 3 dormitorios con unas terminaciones exquisitas situados en Villamartin Golf. Conjunto residencial de lujo, viviendas con solarium de 57.64m2, terrazas de 5.98m2. Estructura formada por vigas y pilares de hormigón armado. Mobiliaria de cocina y encimera (sin electrodomésticos) Carpintería exterior en aluminio con vidrio Climalit y persianas (según tipo). Carpintería interior y armarios. Puerta con cerradura de seguridad Pavimento interior en gres porcelanico. Aislamiento térmico y acústico. Instalación de agua caliente sanitaria a través de placas solares con calentador eléctrico de apoyo. Preinstalación de red de telecomunicaciones según normativa vigente. Aparatos sanitarios en porcelana vitrificada, encimeras de lavabos y plato de ducha con mampara en vidrio. Jardines y piscinas comunitarias. Preinstalación de aire acondicionado. ';
